import styled from "styled-components";

export const Container = styled.div`
  ${({ theme }) => theme.whiteBox};
  width: 100%;
  max-width: 600px;
`;

export const Header = styled.header`
  padding: 15px;
  display: flex;
  align-items: center;
`;

export const UserColumn = styled.div`
  margin-left: 10px;
`;

export const Location = styled.address`
  display: block;
  margin-top: 5px;
  font-size: 12px;
`;
