import { PostId, User, File, CommentId } from "@photo-service/contracts";
import PropTypes from "prop-types";
import { useState } from "react";
import useInput from "src/hooks/useInput";
import { Avatar } from "../Avatar";
import { FatText } from "../FatText";
import { Container, Header, UserColumn, Location } from "./PostItem.styled";

interface PostItemProps {
  id: PostId;
  user: Pick<User, "id" | "avatar" | "userName">;
  files: Array<Pick<File, "id" | "url">>;
  likesCount: number;
  isLiked: boolean;
  location: string | null;
  caption: string;
  comments: Array<{
    id: CommentId;
    text: string;
    user: Pick<User, "id" | "userName">;
  }>;
  createdAt: string | null;
}

export const PostItem: React.FC<PostItemProps> = ({
  id,
  location,
  caption,
  user,
  files,
  likesCount,
  isLiked,
  comments,
  createdAt,
}) => {
  const [isLikedPost, setIsLikedPost] = useState(isLiked);
  const [likesCountPost, setLikesCountPost] = useState(likesCount);
  const comment = useInput("");
  return (
    <Container>
      <Header>
        <Avatar url={user.avatar}></Avatar>
        <UserColumn>
          <FatText>{user.userName}</FatText>
          <Location>{location}</Location>
        </UserColumn>
      </Header>
    </Container>
  );
};

PostItem.propTypes = {
  id: PropTypes.string.isRequired,
  location: PropTypes.string,
  caption: PropTypes.string.isRequired,
  user: PropTypes.shape({
    id: PropTypes.string.isRequired,
    avatar: PropTypes.string,
    userName: PropTypes.string.isRequired,
  }).isRequired,
  files: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      url: PropTypes.string.isRequired,
    }).isRequired
  ).isRequired,
  likesCount: PropTypes.number.isRequired,
  isLiked: PropTypes.bool.isRequired,
  comments: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      text: PropTypes.string.isRequired,
      user: PropTypes.shape({
        id: PropTypes.string.isRequired,
        userName: PropTypes.string.isRequired,
      }).isRequired,
    }).isRequired
  ).isRequired,
  createdAt: PropTypes.string,
};
