import { Post } from "@photo-service/contracts";
import { useQuery } from "react-apollo-hooks";
import { useLocation } from "react-router-dom";
import { FatText } from "src/components/FatText";
import { SEARCH_POSTS } from "./Search.queries";
import { StyledWrapper } from "./Search.styled";

export const Search: React.FC = () => {
  const location = useLocation();

  const query: Record<string, string | undefined> = location.search
    .replace(/\?/, "")
    .split(",")
    .reduce((prev, value) => {
      const param = value.split("=");
      return { ...prev, [param[0]]: param[1] };
    }, {});

  const search = query.s;

  console.log(search);

  const searchPosts = useQuery<{ searchPost: Array<Post> }>(SEARCH_POSTS, {
    variables: { term: search },
  });

  console.log(searchPosts);

  return (
    <StyledWrapper>
      {!search && <FatText>Search for something</FatText>}
      {!!search && <FatText>Search for something</FatText>}
    </StyledWrapper>
  );
};
